/* eslint-disable no-sequences */
import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import LoginForm from "./components/LoginForm";
import Home from "./components/Home";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Metrics from "./routes/Metrics";
import Interview from "./routes/Interview";
import Questions from "./routes/Questions";
import DisplayComponent from './components/DisplayComponent';
import BtnComponent from './components/BtnComponent';
import MetricsOld from "./routes/MetricsOld";
import BarChart from "./BarChart";

function App() {
  const adminUser = {
    email: "cpen@admin.com",
    password: "cpen123",
  };

  const APIUrl = "http://34.222.245.107:5000/api";
  const [eyecontact, setEyecontact] = useState("");
  const [sentiment_score, setSentimentscore] = useState("");
  const [user, setUser] = useState({ firstname: "", lastname: "", email: "" });
  const [error, setError] = useState("");
  const [question, setQuestion] = useState("");
  const [question2, setQuestion2] = useState("");
  const [question3, setQuestion3] = useState("");
  const [question4, setQuestion4] = useState("");
  const [question5, setQuestion5] = useState("");
  const [question6, setQuestion6] = useState("");
  
  var data2 = {
    question: question
  }
  
  var data3 = {
    question: question2
  }
  
  var data4 = {
    question: question3
  }
  
  var data5 = {
    question: question4
  }

  var data6 = {
    question: question5
  }

  var data7 = {
    question: question6
  }

    useEffect(() => {  
      axios
      .get(`${APIUrl}/c`)
      .then(
        (response) => {
          setQuestion([response.data[0].q, response.data[1].q, response.data[2].q, response.data[3].q, response.data[4].q]);
        },
        (reject) => {
          console.log(reject);
        }
      )
      .catch((err) => {
        console.error(err);
      });
      
      axios
      .get(`${APIUrl}/cplus`)
      .then(
        (response) => {
          setQuestion2([response.data[0].q, response.data[1].q, response.data[2].q, response.data[3].q, response.data[4].q]);
        },
        (reject) => {
          console.log(reject);
        }
      )
      .catch((err) => {
        console.error(err);
      });

      axios
      .get(`${APIUrl}/javascript`)
      .then(
        (response) => {
          setQuestion3([response.data[0].q, response.data[1].q, response.data[2].q, response.data[3].q, response.data[4].q]);
        },
        (reject) => {
          console.log(reject);
        }
      )
      .catch((err) => {
        console.error(err);
      });

      axios
      .get(`${APIUrl}/python`)
      .then(
        (response) => {
          setQuestion4([response.data[0].q, response.data[1].q, response.data[2].q, response.data[3].q, response.data[4].q]);
        },
        (reject) => {
          console.log(reject);
        }
      )
      .catch((err) => {
        console.error(err);
      });

      axios
      .get(`${APIUrl}/hardware`)
      .then(
        (response) => {
          setQuestion5([response.data[0].q, response.data[1].q, response.data[2].q, response.data[3].q, response.data[4].q]);
        },
        (reject) => {
          console.log(reject);
        }
      )
      .catch((err) => {
        console.error(err);
      });

      axios
      .get(`${APIUrl}/behavioural`)
      .then(
        (response) => {
          setQuestion6([response.data[0].q, response.data[1].q, response.data[2].q, response.data[3].q, response.data[4].q]);
        },
        (reject) => {
          console.log(reject);
        }
      )
      .catch((err) => {
        console.error(err);
      });
  }, []);

  const Login = (details) => {
    console.log(details);

    if (
      details.email === adminUser.email &&
      details.password === adminUser.password
    ) {
      console.log("Logged in");
      setUser({
        firstname: details.firstname,
        lastname: details.lastname,
        email: details.email,
      });
    } else {
      console.log("Details do not match!");
      setError("Details do not match!");
    }
  };

  const Logout = (details) => {
    setUser({ firstname: "", lastname: "", email: "" });
  };

  
  // STATE to store data to pass to Metrics Component (useState = metrics)
  // callback wrapper function to set the state metrics state 
  const [metrics, setMetrics] = useState({}); 

  const updateMetrics = (metrics) => {
    setMetrics(metrics);
  }

  const [metricsOld, setMetricsOld] = useState({}); 

  const updateMetricsOld = (metricsOld) => {
    setMetricsOld(metricsOld);
  }

  const [userData, setUserData] = useState({
    labels: Metrics.map((data) => data.year),
    datasets: [
      {
        label: "Users Gained",
        data: Metrics.map((data) => data.feedback),
        backgroundColor: [
          "rgba(75,192,192,1)",
          "#ecf0f1",
          "#50AF95",
          "#f3ba2f",
          "#2a71d0",
        ],
        borderColor: "black",
        borderWidth: 2,
      },
    ],
  });


  return (
    <Router>
      <div className="App">
        {user.email !== "" ? (
          <div className="welcome">
            <Home user={user} Logout={Logout} updateMetricsProps={updateMetrics} updateMetricsPropsOld={updateMetricsOld}></Home>
            <div style={{ width: 700 }}>
        <BarChart chartData={userData} />
      </div>
            <Routes>
              <Route path="/interview" element={<Interview />}></Route>
              <Route
                path="/feedback"
                element={
                  <Metrics
                    data={metrics}
                  />
                }
              >
                
              </Route>
              <Route path="/interview" element={<Interview />}></Route>
              <Route
                path="/feedback2"
                element={
                  <MetricsOld
                    data={metricsOld}
                  />
                }
              ></Route>
            <Route path="/interview" element={<Interview />}></Route>
              <Route
                path="/cquestion"
                element={
                  <Questions
                    data={data2}
                  />
                }
              ></Route>
              <Route path="/interview" element={<Interview />}></Route>
              <Route
                path="/cplusquestion"
                element={
                  <Questions
                    data={data3}
                  />
                }
              ></Route>
              <Route path="/interview" element={<Interview />}></Route>
              <Route
                path="/jsquestion"
                element={
                  <Questions
                    data={data4}
                  />
                }
              ></Route>
              <Route path="/interview" element={<Interview />}></Route>
              <Route
                path="/python"
                element={
                  <Questions
                    data={data5}
                  />
                }
              ></Route>
              <Route path="/interview" element={<Interview />}></Route>
              <Route
                path="/hardware"
                element={
                  <Questions
                    data={data6}
                  />
                }
              ></Route>
              <Route path="/interview" element={<Interview />}></Route>
              <Route
                path="/behavioural"
                element={
                  <Questions
                    data={data7}
                  />
                }
              ></Route>
              </Routes>
          </div>
        ) : (
          <Routes>
            <Route
              path="/"
              element={<LoginForm Login={Login} error={error} />}
            ></Route>
          </Routes>
        )}
      </div>
    </Router>
  );
}

export default App;
